<?php $this->layout('page_template', ['title' => 'Register']) ?>

    <h1 class="centerheader">ArmPi <span style="font-size:8px;">(revision T)</span></h1>
    <p style="text-align:center;">Create an account to start reversing ARM binaries</p>

    <?php
      $accounting = $database->querySingle('SELECT setting from settings WHERE name="accounting"');
      if ($accounting != "True") {
        header("Location: home");
      }

      if (!empty($_POST['username']) && !empty($_POST['password']) && !empty($_POST['confirmpassword']) && $accounting == "True") {
        $username = SQLite3::escapeString($_POST["username"]);
        $password = hash('sha256', $_POST["password"]);
        $confirmpassword = hash('sha256', $_POST["confirmpassword"]);
        if ($password == $confirmpassword && (count($database->querySingle('SELECT * FROM auth WHERE username = "'.$username.'"')) != 1)) {
          $database->query('INSERT INTO auth (username,password) VALUES ("'.$username.'","'.$password.'")');
          header("Location: login");
        } elseif (count($database->querySingle('SELECT * FROM auth WHERE username = "'.$username.'"')) == 1) {
          echo "<br /> <p style='text-align:center;'>Account already exists!</p>";
        } else {
          echo "<br /> <p style='text-align:center;'>Passwords don't match!</p>";
        }
      }
    ?>


    <div class="login-form">
        <form action="/register" method="post">
            <h4 class="text-center">Login</h4>
            <div class="form-group">
                <input name="username" type="text" class="form-control" placeholder="Username" required="required" style="background-image: url(&quot;data:image/png;base64,&quot;); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%;" autocomplete="off">
            </div>
            <div class="form-group">
                <input required name="password" type="password" class="form-control" placeholder="Password" required="required" style="background-image: url(&quot;data:image/png;base64,&quot;); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%;" autocomplete="off">
            </div>
            <div class="form-group">
                <input required name="confirmpassword" type="password" class="form-control" placeholder="Confirm Password" required="required" style="background-image: url(&quot;data:image/png;base64,&quot;); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%;" autocomplete="off">
            </div>
            <div class="form-group">
                <button type="submit" class="btn btn-secondary btn-block">Register</button>
            </div>
            <p style="text-align:center;"><a href="login">Back to Login</a></p>
        </form>
    </div>
